import React from 'react';
import fetch from 'isomorphic-unfetch';

import {apiKey} from '../apikey'

class Weather extends React.Component{

    static defaultProps = {
        name: "Enrique"
    }

    constructor(props){
        super(props);

        this.state = {
            zipCode: `22602`,
            countryCode: 'us',
            isLoaded: false,
            data: {
                name: "",
                initial: "",
            },
        }       

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }


    componentDidMount(){
        //useful for Async functions 

        this.getWeather().then( ( data ) => {
            this.setState({
                ...this.state,
                isLoaded: true,
                data: data
            })
        });
        //runs after component renders
        console.log("component did mount")
    }

    componentDidUpdate(prevProps, prevState, snapshot){
        //not in use
        this.getWeather().then( ( data ) => {
            this.setState({
                ...this.state,
                isLoaded: true,
                data: data
            })
        });
        console.log("component did update")
    }



    async getWeather(){
        let APIkey = apiKey.weatherApiKey;
        let URL = `http://api.openweathermap.org/data/2.5/weather?zip=${this.state.zipCode},${this.state.countryCode}&appid=${APIkey}`;

        if (this.state.zipCode.length !== 5) {
            return this.state.data
        }
        try {
            const res = await fetch(URL);
            if (res.ok) {
                const data = await res.json();
                console.log(data)
                return data
            }
            throw new Error('Error Message');    
        }
        catch(err){
            console.log(err)
        }
    }

    handleSubmit(e){
        e.preventDefault();
    }

    handleChange(e){
        this.setState({
            isLoaded: false,
            [e.target.name]: e.target.value
        })
       // 
    }

    renderWidget(){
        return(
            <div>
                <h1>City: {this.state.data.name}</h1>
                <p>Current Temperature: {this.state.data.main.temp}</p>
                <p>Weather: {this.state.data.weather[0].main}</p>
                <p>Humidity: {this.state.data.main.humidity}</p>
                <p>Wind Speed: {this.state.data.wind.speed}</p>
            </div>
        )
    }


    render(){
        console.log("rendered")
        //render method should return JSX
        return(
            <div className = 'Weather'>

                <div>
                    <h1> Weather </h1>
                    <p> - List types of consults</p>
                    <p> - display retrieved information graphically </p>
                    <div> { this.state.isLoaded ? this.renderWidget() : this.state.data.initial} </div>

                    <br></br> 

                    <p></p>
                    <form onSubmit={this.handleSubmit}>
                        <label 
                            className='zipCode-label'
                            htmlFor="zipCode-targeted">NChoose a new zip code: </label>
                        <input  onChange={this.handleChange} 
                            id='zipCode-targeted'
                            type = 'text'
                            name='zipCode'
                            value={this.state.zipCode}
                            ></input>
                        <button className='zipCode-btn'>Submit</button>
                    </form>


                    <br></br> 




                </div>

                <style jsx>
                    {`
                        *{
                            margin: 0;
                            padding: 0;
                            box-sizing: border-box;
                        }

                        .Weather{
                            background-color: rgba(160, 180, 230, .8)
                        }
                    `}
                </style>
            </div>
        )
    }
}

export default Weather;
