import React from 'react';
// import uuid from 'react-uuid'
import fetch from 'isomorphic-unfetch';

class ProjectTemplate extends React.Component{

    constructor(props){
        super(props);

        this.state = {
                projectID: '',
                projectOwner: '',
                projectTitle: '',
                projectDesc: '',
                startDate: '',
                projectScope: '',
                createdOn: '',
            isLoggedon: false
        }       

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);

    }


    componentDidMount(){
        //useful for Async functions 


        //runs after component renders
        console.log("component did mount")
    }

    componentDidUpdate(prevProps, prevState, snapshot){
        //not in use
        console.log("component did update")
    }

    handleChange(e){
        this.setState({
            [e.target.name]: e.target.value
        });
    }

    handleSubmit(e){
        e.preventDefault();

        const createdDate = new Date();

        console.log(createdDate);

        const obj = {
                projectID: '1',
                projectOwner: this.state.projectOwner,
                projectTitle: this.state.projectTitle,
                projectDesc: this.state.projectDesc,
                startDate: this.state.startDate,
                projectScope: this.state.projectScope,
                createdOn: createdDate,
        }

        console.log(obj);

        /*
        if (this.state.isLoggedon) {
            this.setState({
                ...this.state,
                isLoggedon: false
            })
        } else {
            this.setState({
                ...this.state,
                isLoggedon: true
            })
        }
        */
    }    

    render(){
        console.log("rendered")
        //render method should return JSX and styling
        return(
            <div className = 'ProjectTemplate'>

                <div>
                    <h1> Create a Project </h1>

                    <form onSubmit = {this.handleSubmit}>
                        <label className="" htmlFor="new_proj_owner">Project Owner</label>
                        <input onChange={this.handleChange} 
                                id="new_proj_owner" 
                                name="projectOwner" 
                                value={this.state.projectOwner} 
                                type='text'></input>
                        <br></br>
                        <label className="" htmlFor="new_proj_title">Project Title</label>
                        <input onChange={this.handleChange} 
                                id="new_proj_title" 
                                name="projectTitle" 
                                value={this.state.projectTitle} 
                                type='text'></input>
                        <br></br>
                        <label className="" htmlFor="new_proj_desc">Project Desc</label>
                        <input onChange={this.handleChange} 
                                id="new_proj_desc" 
                                name="projectDesc" 
                                value={this.state.projectDesc} 
                                type='text'></input>
                        <br></br>
                        <label className="" htmlFor="new_proj_start_Date">Start Date</label>
                        <input onChange={this.handleChange} 
                                id="new_proj_start_Date" 
                                name="startDate" 
                                value={this.state.startDate} 
                                type='text'></input>
                        <br></br>
                        <label className="" htmlFor="new_proj_scope">Project Scope</label>
                        <input onChange={this.handleChange} 
                                id="new_proj_scope" 
                                name="projectScope" 
                                value={this.state.projectScope} 
                                type='text'></input>
                        <br></br>

                    <button onClick={this.handleSubmit}>Create Project</button>

                    </form>

                </div>

                <style jsx>

                    {`
                        *{
                            margin: 0;
                            padding: 0;
                            box-sizing: border-box;
                        }

                        .ProjectTemplate{

                            background-color: rgba(180, 180, 180, .8)
                        }
                    `}
                </style>
            </div>
        )
    }
}

export default ProjectTemplate;
