import React from 'react';
// import uuid from 'react-uuid'
import fetch from 'isomorphic-unfetch';

class ProjectTemplate extends React.Component{

    constructor(props){
        super(props);

        this.state = {
            project_object: {
                projectID: '',
                projectOwner: '',
                projectTitle: '',
                projectDesc: '',
                startDate: '',
                projectScope: '',
                createdOn: '',
            },
            isLoggedon: false
        }       

        this.handleSubmit = this.handleSubmit.bind(this)
        this.handleChange = this.handleChange.bind(this)

    }


    componentDidMount(){
        //useful for Async functions 


        //runs after component renders
        console.log("component did mount")
    }

    componentDidUpdate(prevProps, prevState, snapshot){
        //not in use
        console.log("component did update")
    }

    handleChange(e){
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    handleSubmit(e){
        e.preventDefault();

        const createdDate = new Date();

        console.log(createdDate);

        const obj = {
                projectID: '1',
                projectOwner: this.state.project_object.projectOwner,
                projectTitle: this.state.project_object.projectTitle,
                projectDesc: this.state.project_object.projectDesc,
                startDate: this.state.project_object.startDate,
                projectScope: this.state.project_object.projectScope,
                createdOn: createdDate,
        }

        /*
        if (this.state.isLoggedon) {
            this.setState({
                ...this.state,
                isLoggedon: false
            })
        } else {
            this.setState({
                ...this.state,
                isLoggedon: true
            })
        }
        */
    }    

    render(){
        console.log("rendered")
        //render method should return JSX and styling
        return(
            <div className = 'ProjectTemplate'>

                <div>
                    <h1> Create a Project </h1>

                    <form onSubmit = {this.handleSubmit}>
                        <label className="" htmlFor="projectOwner">Project Owner</label>
                        <input onChange={this.handleChange} id="" name="projectOwner" value={this.state.project_object.projectOwner} type='text'></input>
                        <br></br>
                        <label className="" htmlFor="projectTitle">Project Title</label>
                        <input onChange={this.handleChange} id="" name="projectTitle" value={this.state.project_object.projectTitle} type='text'></input>
                        <br></br>
                        <label className="" htmlFor="projectDesc">Project Desc</label>
                        <input onChange={this.handleChange} id="" name="projectDesc" value={this.state.project_object.projectDesc} type='text'></input>
                        <br></br>
                        <label className="" htmlFor="startDate">Start Date</label>
                        <input onChange={this.handleChange} id="" name="startDate" value={this.state.project_object.startDate} type='text'></input>
                        <br></br>
                        <label className="" htmlFor="projectScope">Project Scope</label>
                        <input onChange={this.handleChange} id="" name="projectScope" value={this.state.project_object.projectScope} type='text'></input>
                        <br></br>

                    <button onClick={this.handleSubmit}>Create Project</button>

                    </form>

                </div>

                <style jsx>

                    {`
                        *{
                            margin: 0;
                            padding: 0;
                            box-sizing: border-box;
                        }

                        .ProjectTemplate{

                            background-color: rgba(160, 180, 230, .8)
                        }
                    `}
                </style>
            </div>
        )
    }
}

export default ProjectTemplate;
