import React from 'react';
// import uuid from 'react-uuid'
import fetch from 'isomorphic-unfetch';
import ProjectList from '../Components/ProjectList';
import ProjectTemplate from '../Components/ProjectTemplate';
import {projects_array} from '../sample_projects';

class ProjectModule extends React.Component{

    constructor(props){
        super(props);

        this.state = {
                projectID: '',
                projectOwner: '',
                projectTitle: '',
                projectDesc: '',
                startDate: '',
                projectScope: '',
                createdOn: '',
            isLoggedon: false
        }       

        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);

    }


    componentDidMount(){
        //useful for Async functions 


        //runs after component renders
        console.log("component did mount")
    }

    componentDidUpdate(prevProps, prevState, snapshot){
        //not in use
        console.log("component did update")
    }

    handleChange(e){
        this.setState({
            [e.target.name]: e.target.value
        });
    }

    handleSubmit(e){
        e.preventDefault();

        const createdDate = new Date();

        console.log(createdDate);

        const obj = {
                projectID: '1',
                projectOwner: this.state.projectOwner,
                projectTitle: this.state.projectTitle,
                projectDesc: this.state.projectDesc,
                startDate: this.state.startDate,
                projectScope: this.state.projectScope,
                createdOn: createdDate,
        }

        console.log(obj);

        /*
        if (this.state.isLoggedon) {
            this.setState({
                ...this.state,
                isLoggedon: false
            })
        } else {
            this.setState({
                ...this.state,
                isLoggedon: true
            })
        }
        */
    }    

    render(){
        console.log("rendered")
        //render method should return JSX and styling
        return(
            <div className = 'ProjectModule'>


                    <h1> Create a Project </h1>

                  <div className='Proj-module-search'>
                    <h2>Search Projects</h2>
                    <input id='proj-search-input'></input>
                  </div>

                    <nav>
                        <ul>
                            <li>New Project</li>
                            <li>View/Edit Project</li>
                        </ul>
                    </nav>

                    <ProjectList projects={projects_array}/>

                    <ProjectTemplate />             



                <style jsx>

                    {`

                        .ProjectModule {
                            background-color: rgba(0, 50, 30, .8);
                            display: flex;
                            flex-direction: column;

                            border-radius: 3px;
                            color: rgba(250, 250, 250, .9);

                            margin-top: 2rem;
                            padding: 2rem 1rem 2rem 1rem;



                        }

                        .Proj-module-search {
                            display: flex;
                            flex-direction: row;

                            align-items: center;
                            justify-content: space-between;

                            font-size: 1.2rem;
                            margin-right: 2rem;
                        }

                        #proj-search-input {
                            height: 2rem;
                            width: 300px;
                        }


                    `}
                </style>
            </div>
        )
    }
}

export default ProjectModule;
